const navBtn=document.querySelector(".nav__btn"),navMenu=document.querySelector(".nav__wrapper"),navLinks=document.querySelectorAll(".nav__menu-item");var swiper=new Swiper(".header__swiper",{slidesPerView:1,spaceBetween:30,loop:!0,pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});navBtn.addEventListener("click",()=>{navMenu.classList.toggle("active"),navLinks.forEach(e=>{e.addEventListener("click",()=>{navMenu.classList.remove("active")})})});